import { useState, useEffect } from 'react';

function Tweet() {
  const [getBound , setGetBound] = useState();
  const [scroll , setScroll] = useState();
  const [show , setShow] = useState();

  useEffect(() => {
    const tweetEl = document.getElementById('tweet');
    setGetBound(tweetEl.getBoundingClientRect().top);

    if (getBound <= window.innerHeight) {
      setShow(true);
    }
  },[scroll]);

  window.addEventListener('scroll',()=>{
      setScroll(window.scrollY);
  });

  return (          
    <iframe
      id='tweet'
      src={show && 'https://syndication.twitter.com/srv/timeline-profile/screen-name/Theme_Fusion?dnt=true&embedId=twitter-widget-0&%3D%3D&frame=false&hideBorder=true&hideFooter=true&hideHeader=true&hideScrollBar=false&lang=en&limit=1&maxHeight=400px&origin=https%3A%2F%2Favada.website%2Fconstruction%2Fget-quote%2F&sessionId=2cc9c9227d59c29ba31c09fa7e417b4c44d4650c&showHeader=false&showReplies=false&theme=dark&transparent=true&widgetsVersion=aaf4084522e3a%3A1674595607486'}
    ></iframe>
  );
}

export default Tweet;